// Update uses an executor to update the SlotRecord.
// Whitelist behavior: If a whitelist is provided, only the columns given are updated.
// No whitelist behavior: Without a whitelist, columns are inferred by the following rules:
// - All columns are inferred to start with
// - All primary keys are subtracted from this set
// Update does not automatically update the record in case of default values. Use .Reload()
// to refresh the records.
func (o *SlotRecord) Update(exec boil.Executor, whitelist ...string) error {
	var err error
	if err = o.doBeforeUpdateHooks(exec); err != nil {
		return err
	}
	key := makeCacheKey(whitelist, nil)
	slotRecordUpdateCacheMut.RLock()
	cache, cached := slotRecordUpdateCache[key]
	slotRecordUpdateCacheMut.RUnlock()

	if !cached {
		wl := strmangle.UpdateColumnSet(slotRecordColumns, slotRecordPrimaryKeyColumns, whitelist)

		if len(whitelist) == 0 {
			wl = strmangle.SetComplement(wl, []string{"created_at"})
		}
		if len(wl) == 0 {
			return errors.New("public: unable to update slot_record, could not build whitelist")
		}

		cache.query = fmt.Sprintf("UPDATE \"slot_record\" SET %s WHERE %s",
			strmangle.SetParamNames("\"", "\"", 1, wl),
			strmangle.WhereClause("\"", "\"", len(wl)+1, slotRecordPrimaryKeyColumns),
		)
		cache.valueMapping, err = queries.BindMapping(slotRecordType, slotRecordMapping, append(wl, slotRecordPrimaryKeyColumns...))
		if err != nil {
			return err
		}
	}

	values := queries.ValuesFromMapping(reflect.Indirect(reflect.ValueOf(o)), cache.valueMapping)

	if boil.DebugMode {
		fmt.Fprintln(boil.DebugWriter, cache.query)
		fmt.Fprintln(boil.DebugWriter, values)
	}

	_, err = exec.Exec(cache.query, values...)
	if err != nil {
		return errors.Wrap(err, "public: unable to update slot_record row")
	}

	if !cached {
		slotRecordUpdateCacheMut.Lock()
		slotRecordUpdateCache[key] = cache
		slotRecordUpdateCacheMut.Unlock()
	}

	return o.doAfterUpdateHooks(exec)
}

